    // combat is over. Clear shit out and go to main
    public cleanupAfterCombat(nextFunc?: any): void {
        if (nextFunc == undefined) nextFunc = this.camp.returnToCampUseOneHour;
        if (this.inCombat) {
            // clear status
            this.clearStatuses(false);
            // Clear itemswapping in case it hung somehow
            // No longer used:  itemSwapping = false;
            // Player won
            if (this.monster.HP < 1 || this.monster.lust > 99) {
                this.awardPlayer();
            }
            // Player lost
            else {
                if (this.monster.statusAffectv1(StatusAffects.Sparring) == 2) {
                    this.outx("The cow-girl has defeated you in a practice fight!", true);
                    this.outx(
                        "\n\nYou have to lean on Isabella's shoulder while the two of your hike back to camp.  She clearly won.",
                        false
                    );
                    this.inCombat = false;
                    this.player.HP = 1;
                    this.statScreenRefresh();
                    this.doNext(nextFunc);
                    return;
                }
                // Next button is handled within the minerva loss function
                if (this.monster.findStatusAffect(StatusAffects.PeachLootLoss) >= 0) {
                    this.inCombat = false;
                    this.player.HP = 1;
                    this.statScreenRefresh();
                    return;
                }
                if (this.monster.short == "Ember") {
                    this.inCombat = false;
                    this.player.HP = 1;
                    this.statScreenRefresh();
                    this.doNext(nextFunc);
                    return;
                }
                this.temp = this.rand(10) + 1 + Math.round(this.monster.level / 2);
                if (this.inDungeon) this.temp += 20 + this.monster.level * 2;
                if (this.temp > this.player.gems) this.temp = this.player.gems;
                const timePasses: number = this.monster.handleCombatLossText(
                    this.inDungeon,
                    this.temp
                ); // Allows monsters to customize the loss text and the amount of time lost
                this.player.gems -= this.temp;
                this.inCombat = false;
                // BUNUS XPZ
                if (this.flags[kFLAGS.COMBAT_BONUS_XP_VALUE] > 0) {
                    this.player.XP += this.flags[kFLAGS.COMBAT_BONUS_XP_VALUE];
                    this.outx(
                        `  Somehow you managed to gain ${
                            this.flags[kFLAGS.COMBAT_BONUS_XP_VALUE]
                        } XP from the situation.`
                    );
                    this.flags[kFLAGS.COMBAT_BONUS_XP_VALUE] = 0;
                }
                // Bonus lewts
                if (this.flags[kFLAGS.BONUS_ITEM_AFTER_COMBAT_ID] != "") {
                    this.outx(
                        `  Somehow you came away from the encounter with ${
                            ItemType.lookupItem(this.flags[kFLAGS.BONUS_ITEM_AFTER_COMBAT_ID])
                                .longName
                        }.\n\n`
                    );
                    this.inventory.takeItem(
                        ItemType.lookupItem(this.flags[kFLAGS.BONUS_ITEM_AFTER_COMBAT_ID]),
                        this.createCallBackFunction(this.camp.returnToCamp, timePasses)
                    );
                } else this.doNext(this.createCallBackFunction(this.camp.returnToCamp, timePasses));
            }
        }
        // Not actually in combat
        else this.doNext(nextFunc);
    }

    public approachAfterKnockback(): void {
        this.clearOutput();
        this.outx(
            `You close the distance between you and ${this.monster.a}${this.monster.short} as quickly as possible.\n\n`
        );
        this.player.removeStatusAffect(StatusAffects.KnockedBack);
        this.enemyAI();
        return;
    }

    private canUseMagic(): boolean {
        if (this.player.findStatusAffect(StatusAffects.ThroatPunch) >= 0) return false;
        if (this.player.findStatusAffect(StatusAffects.WebSilence) >= 0) return false;
        if (this.player.findStatusAffect(StatusAffects.GooArmorSilence) >= 0) return false;
        return true;
    }

