    public bite(): void {
        this.flags[kFLAGS.LAST_ATTACK_TYPE] = 2;
        if (this.player.fatigue + this.physicalCost(25) > 100) {
            this.outx("You're too fatigued to use your shark-like jaws!", true);
            this.menu();
            this.addButton(0, "Next", this.combatMenu, false);
            return;
        }
        // Worms are special
        if (this.monster.short == "worms") {
            this.outx("There is no way those are going anywhere near your mouth!\n\n", true);
            this.menu();
            this.addButton(0, "Next", this.combatMenu, false);
            return;
        }
        this.fatigue(25, 2);
        // Amily!
        if (this.monster.findStatusAffect(StatusAffects.Concentration) >= 0) {
            this.outx(
                "Amily easily glides around your attack thanks to her complete concentration on your movements.\n\n",
                true
            );
            this.enemyAI();
            return;
        }
        this.outx(
            "You open your mouth wide, your shark teeth extending out. Snarling with hunger, you lunge at your opponent, set to bite right into them!  ",
            true
        );
        if (this.player.findStatusAffect(StatusAffects.Blind) >= 0)
            this.outx(
                "In hindsight, trying to bite someone while blind was probably a bad idea... ",
                false
            );
        let damage = 0;
        // Determine if dodged!
        if (
            (this.player.findStatusAffect(StatusAffects.Blind) >= 0 && this.rand(3) != 0) ||
            (this.monster.spe - this.player.spe > 0 &&
                int(Math.random() * ((this.monster.spe - this.player.spe) / 4 + 80)) > 80)
        ) {
            if (this.monster.spe - this.player.spe < 8)
                this.outx(
                    `${this.monster.capitalA + this.monster.short} narrowly avoids your attack!`
                );
            if (this.monster.spe - this.player.spe >= 8 && this.monster.spe - this.player.spe < 20)
                this.outx(
                    `${
                        this.monster.capitalA + this.monster.short
                    } dodges your attack with superior quickness!`
                );
            if (this.monster.spe - this.player.spe >= 20)
                this.outx(
                    `${this.monster.capitalA + this.monster.short} deftly avoids your slow attack.`
                );
            this.outx("\n\n", false);
            this.enemyAI();
            return;
        }
        // Determine damage - str modified by enemy toughness!
        damage = int(this.player.str + 45 - this.rand(this.monster.tou) - this.monster.armorDef);

        // Deal damage and update based on perks
        if (damage > 0) {
            if (this.player.findPerk(PerkLib.HistoryFighter) >= 0) damage *= 1.1;
            damage = this.doDamage(damage);
        }

        if (damage <= 0) {
            damage = 0;
            this.outx(
                `Your bite is deflected or blocked by ${this.monster.a}${this.monster.short}.`
            );
        }
        if (damage > 0 && damage < 10) {
            this.outx(
                `You bite doesn't do much damage to ${this.monster.a}${this.monster.short}! (${damage})`
            );
        }
        if (damage >= 10 && damage < 20) {
            this.outx(
                `You seriously wound ${this.monster.a}${this.monster.short} with your bite! (${damage})`
            );
        }
        if (damage >= 20 && damage < 30) {
            this.outx(
                `Your bite staggers ${this.monster.a}${this.monster.short} with its force. (${damage})`
            );
        }
        if (damage >= 30) {
            this.outx(
                `Your powerful bite <b>mutilates</b> ${this.monster.a}${this.monster.short}! (${damage})`
            );
        }
        this.outx("\n\n", false);
        // Kick back to main if no damage occured!
        if (this.monster.HP > 0 && this.monster.lust < 100) {
            this.enemyAI();
        } else {
            if (this.monster.HP <= 0) this.doNext(this.endHpVictory);
            else this.doNext(this.endLustVictory);
        }
    }

