    public kick(): void {
        this.flags[kFLAGS.LAST_ATTACK_TYPE] = 4;
        this.outx("", true);
        if (this.player.fatigue + this.physicalCost(15) > 100) {
            this.outx("You're too fatigued to use a charge attack!", true);
            this.menu();
            this.addButton(0, "Next", this.combatMenu, false);
            return;
        }
        this.fatigue(15, 2);
        // Variant start messages!
        if (this.player.lowerBody == CoC.LOWER_BODY_TYPE_KANGAROO) {
            // (tail)
            if (this.player.tailType == CoC.TAIL_TYPE_KANGAROO)
                this.outx(
                    "You balance on your flexible kangaroo-tail, pulling both legs up before slamming them forward simultaneously in a brutal kick.  ",
                    false
                );
            // (no tail)
            else
                this.outx(
                    "You balance on one leg and cock your powerful, kangaroo-like leg before you slam it forward in a kick.  ",
                    false
                );
        }
        // (bunbun kick)
        else if (this.player.lowerBody == CoC.LOWER_BODY_TYPE_BUNNY)
            this.outx(
                "You leap straight into the air and lash out with both your furred feet simultaneously, slamming forward in a strong kick.  ",
                false
            );
        // (centaur kick)
        else if (this.player.lowerBody == CoC.LOWER_BODY_TYPE_CENTAUR)
            this.outx(
                "You lurch up onto your backlegs, lifting your forelegs from the ground a split-second before you lash them out in a vicious kick.  ",
                false
            );
        // (bipedal hoof-kick)
        else if (this.player.lowerBody == CoC.LOWER_BODY_TYPE_HOOFED)
            this.outx(
                "You twist and lurch as you raise a leg and slam your hoof forward in a kick.  ",
                false
            );

        if (this.flags[kFLAGS.PC_FETISH] >= 3) {
            this.outx(
                "You attempt to attack, but at the last moment your body wrenches away, preventing you from even coming close to landing a blow!  Ceraph's piercings have made normal attack impossible!  Maybe you could try something else?\n\n",
                false
            );
            this.enemyAI();
            return;
        }
        // Amily!
        if (this.monster.findStatusAffect(StatusAffects.Concentration) >= 0) {
            this.outx(
                "Amily easily glides around your attack thanks to her complete concentration on your movements.\n\n",
                true
            );
            this.enemyAI();
            return;
        }
        // Blind
        if (this.player.findStatusAffect(StatusAffects.Blind) >= 0) {
            this.outx(
                "You attempt to attack, but as blinded as you are right now, you doubt you'll have much luck!  ",
                false
            );
        }
        // Worms are special
        if (this.monster.short == "worms") {
            // 50% chance of hit (int boost)
            if (this.rand(100) + this.player.inte / 3 >= 50) {
                this.temp = int(this.player.str / 5 - this.rand(5));
                if (this.temp == 0) this.temp = 1;
                this.outx(
                    `You strike at the amalgamation, crushing countless worms into goo, dealing ${this.temp} damage.\n\n`,
                    false
                );
                this.monster.HP -= this.temp;
                if (this.monster.HP <= 0) {
                    this.doNext(this.endHpVictory);
                    return;
                }
            }
            // Fail
            else {
                this.outx(
                    "You attempt to crush the worms with your reprisal, only to have the collective move its individual members, creating a void at the point of impact, leaving you to attack only empty air.\n\n",
                    false
                );
            }
            this.enemyAI();
            return;
        }
        let damage: number;
        // Determine if dodged!
        if (
            (this.player.findStatusAffect(StatusAffects.Blind) >= 0 && this.rand(2) == 0) ||
            (this.monster.spe - this.player.spe > 0 &&
                int(Math.random() * ((this.monster.spe - this.player.spe) / 4 + 80)) > 80)
        ) {
            // Akbal dodges special education
            if (this.monster.short == "Akbal")
                this.outx(
                    "Akbal moves like lightning, weaving in and out of your furious attack with the speed and grace befitting his jaguar body.\n",
                    false
                );
            else {
                this.outx(`${this.monster.capitalA + this.monster.short} manage`);
                if (!this.monster.plural) this.outx("s");
                this.outx(" to dodge your kick!");
                this.outx("\n\n", false);
            }
            this.enemyAI();
            return;
        }
        // Determine damage
        // Base:
        damage = this.player.str;
        // Leg bonus
        // Bunny - 20, Kangaroo - 35, 1 hoof = 30, 2 hooves = 40
        if (this.player.lowerBody == CoC.LOWER_BODY_TYPE_CENTAUR) damage += 40;
        else if (this.player.lowerBody == CoC.LOWER_BODY_TYPE_HOOFED) damage += 30;
        else if (this.player.lowerBody == CoC.LOWER_BODY_TYPE_BUNNY) damage += 20;
        else if (this.player.lowerBody == CoC.LOWER_BODY_TYPE_KANGAROO) damage += 35;
        // Start figuring enemy damage resistance
        let reduction: number = this.rand(this.monster.tou);
        // Add in enemy armor if needed
        reduction += this.monster.armorDef;
        // Apply AND DONE!
        damage -= reduction;
        // Damage post processing!
        if (this.player.findPerk(PerkLib.HistoryFighter) >= 0) damage *= 1.1;
        // (None yet!)
        if (damage > 0) damage = this.doDamage(damage);

        // BLOCKED
        if (damage <= 0) {
            damage = 0;
            this.outx(this.monster.capitalA + this.monster.short, false);
            if (this.monster.plural) this.outx("'");
            else this.outx("s");
            this.outx(" defenses are too tough for your kick to penetrate!");
        }
        // LAND A HIT!
        else {
            this.outx(this.monster.capitalA + this.monster.short, false);
            if (!this.monster.plural) this.outx(` reels from the damaging impact! (${damage})`);
            else this.outx(` reel from the damaging impact! (${damage})`);
        }
        if (damage > 0) {
            // Lust raised by anemone contact!
            if (this.monster.short == "anemone") {
                this.outx(
                    "\nThough you managed to hit the anemone, several of the tentacles surrounding her body sent home jolts of venom when your swing brushed past them.",
                    false
                );
                // (gain lust, temp lose str/spd)
                (this.monster as Anemone).applyVenom(1 + this.rand(2));
            }
        }
        this.outx("\n\n", false);
        if (this.monster.HP < 1 || this.monster.lust > 99) this.combatRoundOver();
        else this.enemyAI();
    }

