    // Terror
    public kitsuneTerror(): void {
        this.clearOutput();
        // Fatigue Cost: 25
        if (
            this.player.findPerk(PerkLib.BloodMage) < 0 &&
            this.player.fatigue + this.spellCost(20) > 100
        ) {
            this.outx("You are too tired to use this ability.", true);
            this.doNext(this.magicalSpecials);
            return;
        }
        if (this.monster.findStatusAffect(StatusAffects.Shell) >= 0) {
            this.outx(
                `As soon as your magic touches the multicolored shell around ${this.monster.a}${this.monster.short}, it sizzles and fades to nothing.  Whatever that thing is, it completely blocks your magic!\n\n`
            );
            this.enemyAI();
            return;
        }
        if (
            this.player.findStatusAffect(StatusAffects.ThroatPunch) >= 0 ||
            this.player.findStatusAffect(StatusAffects.WebSilence) >= 0
        ) {
            this.outx(
                "You cannot focus to reach the enemy's mind while you're having so much difficult breathing.",
                true
            );
            this.doNext(this.magicalSpecials);
            return;
        }
        if (this.monster.short == "pod" || this.monster.inte == 0) {
            this.outx(
                "You reach for the enemy's mind, but cannot find anything.  You frantically search around, but there is no consciousness as you know it in the room.\n\n",
                true
            );
            this.changeFatigue(1);
            this.enemyAI();
            return;
        }
        // This is now automatic - newRound arg defaults to true: menuLoc = 0;
        this.fatigue(20, 1);
        // Inflicts fear and reduces enemy SPD.
        this.outx(
            `The world goes dark, an inky shadow blanketing everything in sight as you fill ${this.monster.a}${this.monster.short}'s mind with visions of otherworldly terror that defy description.`
        );
        // (succeed)
        if (this.player.inte / 10 + this.rand(20) + 1 > this.monster.inte / 10 + 10) {
            this.outx(
                "  They cower in horror as they succumb to your illusion, believing themselves beset by eldritch horrors beyond their wildest nightmares.\n\n"
            );
            this.monster.createStatusAffect(StatusAffects.Fear, 1, 0, 0, 0);
            this.monster.spe -= 5;
            if (this.monster.spe < 1) this.monster.spe = 1;
        } else
            this.outx(
                "  The dark fog recedes as quickly as it rolled in as they push back your illusions, resisting your hypnotic influence.\n\n"
            );
        this.enemyAI();
    }

