    public HPChange(changeNum: number, display: boolean): void {
        if (changeNum == 0) return;
        if (changeNum > 0) {
            // Increase by 20%!
            if (this.player.findPerk(PerkLib.HistoryHealer) >= 0) changeNum *= 1.2;
            if (this.player.HP + Math.floor(changeNum) > this.maxHP()) {
                if (this.player.HP >= this.maxHP()) {
                    if (display) this.outx("You're as healthy as you can be.\n", false);
                    return;
                }
                if (display) this.outx(`Your HP maxes out at ${this.maxHP()}.\n`, false);
                this.player.HP = this.maxHP();
            } else {
                if (display) this.outx(`You gain ${Math.floor(changeNum)} HP.\n`, false);
                this.player.HP += Math.floor(changeNum);
                this.mainView.statsView.showStatUp("hp");
                // hpUp.visible = true;
            }
        }
        // Negative HP
        else {
            if (this.player.HP + changeNum <= 0) {
                if (display)
                    this.outx(
                        `You take ${Math.floor(changeNum * -1)} damage, dropping your HP to 0.\n`,
                        false
                    );
                this.player.HP = 0;
            } else {
                if (display) this.outx(`You take ${Math.floor(changeNum * -1)} damage.\n`, false);
                this.player.HP += changeNum;
            }
        }
        this.statScreenRefresh();
    }

